import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import DrawingCanvas from "./DrawingCanvas";
import Toolbar from "./Toolbar";
import UserCursors from "./UserCursors";

const SOCKET_URL =
  process.env.REACT_APP_SOCKET || window.location.origin.replace(/^http/, "ws");

export default function Whiteboard({ roomId, onLeave }) {
  const [socket, setSocket] = useState(null);
  const [usersCount, setUsersCount] = useState(1);
  const [cursors, setCursors] = useState({});
  const colorRef = useRef("#000");

  useEffect(() => {
    const s = io(SOCKET_URL, { transports: ["websocket"] });
    setSocket(s);

    s.on("connect", () => {
      s.emit("join-room", { roomId });
    });

    s.on("user-count", ({ count }) => setUsersCount(count || 1));
    s.on("init-color", ({ color }) => (colorRef.current = color));

    s.on("cursor-move", ({ socketId, x, y, color }) => {
      setCursors((prev) => ({
        ...prev,
        [socketId]: { x, y, color, last: Date.now() },
      }));
    });

    s.on("user-left", ({ socketId }) => {
      setCursors((prev) => {
        const n = { ...prev };
        delete n[socketId];
        return n;
      });
    });

    return () => {
      if (s) {
        s.emit("leave-room", { roomId });
        s.disconnect();
      }
    };
  }, [roomId]);

  // Remove stale cursors
  useEffect(() => {
    const t = setInterval(() => {
      const now = Date.now();
      setCursors((prev) => {
        const n = { ...prev };
        for (const k of Object.keys(n)) {
          if (now - n[k].last > 3000) delete n[k];
        }
        return n;
      });
    }, 1000);
    return () => clearInterval(t);
  }, []);

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      {/* Topbar */}
      <header className="flex items-center justify-between px-6 py-3 bg-white border-b shadow-sm">
        <div className="text-sm text-gray-700">
          Room:{" "}
          <strong className="font-semibold text-gray-900">{roomId}</strong> Â·{" "}
          <span className="text-gray-600">Users: {usersCount}</span>
        </div>
        <button
          onClick={onLeave}
          className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white text-sm font-medium rounded-md shadow transition"
        >
          Leave Room
        </button>
      </header>

      {/* Canvas Area */}
      <main className="flex-1 relative overflow-hidden">
        <DrawingCanvas socket={socket} roomId={roomId} myColorRef={colorRef} />
        <UserCursors cursors={cursors} />
      </main>

      {/* Toolbar (bottom floating) */}
      <div className="absolute bottom-6 left-1/2 -translate-x-1/2">
        <div className="bg-white/90 backdrop-blur-md shadow-lg border border-gray-200 rounded-2xl px-6 py-3">
          <Toolbar socket={socket} roomId={roomId} />
        </div>
      </div>
    </div>
  );
}
