import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import DrawingCanvas from './DrawingCanvas';
import Toolbar from './Toolbar';
import UserCursors from './UserCursors';

const SOCKET_URL = process.env.REACT_APP_SOCKET || (window.location.origin.replace(/^http/, 'ws'));

export default function Whiteboard({ roomId, onLeave }){
  const [socket, setSocket] = useState(null);
  const [usersCount, setUsersCount] = useState(1);
  const [cursors, setCursors] = useState({});
  const colorRef = useRef('#000');

  useEffect(()=>{
    const s = io(SOCKET_URL, { transports: ['websocket'] });
    setSocket(s);
    s.on('connect', ()=>{ s.emit('join-room', { roomId }); });
    s.on('user-count', ({count})=> setUsersCount(count || 1));
    s.on('init-color', ({color})=> colorRef.current = color);
    s.on('cursor-move', ({ socketId, x, y, color })=>{
      setCursors(prev => ({ ...prev, [socketId]: { x, y, color, last: Date.now() } }));
    });
    s.on('user-left', ({ socketId })=>{
      setCursors(prev=>{ const n={...prev}; delete n[socketId]; return n; });
    });
    return ()=>{ if(s){ s.emit('leave-room', { roomId }); s.disconnect(); } };
  }, [roomId]);

  useEffect(()=>{
    const t = setInterval(()=>{
      const now = Date.now();
      setCursors(prev=>{
        const n = {...prev};
        for(const k of Object.keys(n)){
          if(now - n[k].last > 3000) delete n[k];
        }
        return n;
      });
    },1000);
    return ()=>clearInterval(t);
  },[]);

  return (
    <div className="whiteboard-wrap">
      <div className="topbar">
        <div className="count">Room: <strong>{roomId}</strong> â€” Users: {usersCount}</div>
        <div style={{display:'flex',gap:8}}>
          <button className="btn" onClick={()=>onLeave()}>Leave</button>
        </div>
      </div>
      <div className="canvas-area">
        <DrawingCanvas socket={socket} roomId={roomId} myColorRef={colorRef} />
        <UserCursors cursors={cursors} />
      </div>
      <div className="toolbar">
        <Toolbar socket={socket} roomId={roomId} />
      </div>
    </div>
  );
}
