import React, { useState } from 'react';
import axios from 'axios';
function randomRoom(){ return Math.random().toString(36).slice(2,8).toUpperCase(); }
export default function RoomJoin({ onJoin }){
  const [code, setCode] = useState('');
  const [error, setError] = useState('');
  const join = async (r) => {
    const rid = r || code.trim();
    if(!rid){ setError('Enter room code or generate one'); return; }
    try{
      await axios.post((process.env.REACT_APP_API || '') + '/api/rooms/join', { roomId: rid });
      onJoin(rid);
    }catch(e){ setError('Unable to join room'); }
  };
  return (
    <div className="join">
      <h2>Join a Whiteboard Room</h2>
      <div className="input-row">
        <input value={code} onChange={e=>setCode(e.target.value.toUpperCase())} placeholder="Room code (6-8 chars)" maxLength={8} />
        <button className="btn" onClick={()=>join()}>Join</button>
      </div>
      <div style={{display:'flex',gap:8}}>
        <button className="btn" onClick={()=>{ const r=randomRoom(); setCode(r); join(r); }}>Create Room</button>
        <button className="btn" onClick={()=>{ const r=randomRoom(); setCode(r); }}>Generate</button>
      </div>
      {error && <p style={{color:'red'}}>{error}</p>}
    </div>
  );
}
