import React, { useEffect, useRef, useState } from "react";
import { FiDroplet, FiEdit3, FiTrash2 } from "react-icons/fi";

export default function DrawingCanvas({ socket, roomId }) {
  const canvasRef = useRef();
  const [isDrawing, setIsDrawing] = useState(false);
  const [color, setColor] = useState("#000000");
  const [width, setWidth] = useState(2);
  const pathRef = useRef([]);

  useEffect(() => {
    const c = canvasRef.current;
    const ctx = c.getContext("2d");

    const onLoadCommands = (commands) => {
      commands.forEach((cmd) => {
        if (cmd.type === "stroke") {
          drawStroke(ctx, cmd.data, false);
        } else if (cmd.type === "clear") {
          ctx.clearRect(0, 0, c.width, c.height);
        }
      });
    };

    if (socket) {
      socket.on("load-commands", onLoadCommands);
      socket.on("draw-start", ({ payload }) => beginRemoteStroke(ctx, payload));
      socket.on("draw-move", ({ payload }) => moveRemoteStroke(ctx, payload));
      socket.on("draw-end", ({ command }) => endRemoteStroke(ctx, command));
      socket.on("clear-canvas", () =>
        ctx.clearRect(0, 0, c.width, c.height)
      );
    }

    return () => {
      if (socket) socket.off("load-commands", onLoadCommands);
    };
  }, [socket]);

  // Drawing Helpers
  function drawStroke(ctx, data) {
    ctx.lineJoin = "round";
    ctx.lineCap = "round";
    ctx.strokeStyle = data.color;
    ctx.lineWidth = data.width;
    ctx.beginPath();
    const path = data.path || [];
    if (path.length === 0) return;
    ctx.moveTo(path[0].x, path[0].y);
    for (let i = 1; i < path.length; i++) ctx.lineTo(path[i].x, path[i].y);
    ctx.stroke();
  }

  function beginRemoteStroke(ctx, payload) {
    drawStroke(ctx, {
      color: payload.color,
      width: payload.width,
      path: [payload.point],
    });
  }
  function moveRemoteStroke(ctx, payload) {
    drawStroke(ctx, {
      color: payload.color,
      width: payload.width,
      path: payload.path,
    });
  }
  function endRemoteStroke(ctx, command) {
    drawStroke(ctx, command.data);
  }

  function getPos(e) {
    const rect = canvasRef.current.getBoundingClientRect();
    const clientX = e.touches ? e.touches[0].clientX : e.clientX;
    const clientY = e.touches ? e.touches[0].clientY : e.clientY;
    return { x: clientX - rect.left, y: clientY - rect.top };
  }

  function start(e) {
    if (!socket) return;
    const p = getPos(e);
    setIsDrawing(true);
    pathRef.current = [p];
    socket.emit("draw-start", { roomId, payload: { point: p, color, width } });
  }

  function draw(e) {
    if (!isDrawing || !socket) return;
    const p = getPos(e);
    pathRef.current.push(p);
    socket.emit("draw-move", { roomId, payload: { path: [p], color, width } });
    const ctx = canvasRef.current.getContext("2d");
    drawStroke(ctx, { color, width, path: pathRef.current });
    socket.emit("cursor-move", { roomId, x: p.x, y: p.y });
  }

  function end() {
    if (!isDrawing || !socket) return;
    setIsDrawing(false);
    const command = {
      type: "stroke",
      data: { path: pathRef.current, color, width },
      timestamp: new Date(),
    };
    socket.emit("draw-end", { roomId, command });
    pathRef.current = [];
  }

  function clearCanvas() {
    const ctx = canvasRef.current.getContext("2d");
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    if (socket) socket.emit("clear-canvas", { roomId });
  }

  return (
    <div className="relative w-full h-full bg-gray-50">
      {/* Drawing Canvas */}
      <canvas
        ref={canvasRef}
        className="w-full h-full cursor-crosshair bg-white shadow-inner rounded-lg"
        onMouseDown={start}
        onMouseMove={draw}
        onMouseUp={end}
        onTouchStart={start}
        onTouchMove={draw}
        onTouchEnd={end}
      />

      {/* Toolbar */}
      <div className="absolute top-4 left-4 flex flex-col gap-4 bg-white shadow-xl rounded-xl p-4 border border-gray-200">
        {/* Color Picker */}
        <label className="flex items-center gap-2 text-sm font-medium text-gray-700">
          <FiDroplet className="text-blue-500" />
          <input
            type="color"
            value={color}
            onChange={(e) => setColor(e.target.value)}
            className="w-10 h-10 rounded cursor-pointer border border-gray-300"
          />
        </label>

        {/* Stroke Width */}
        <label className="flex items-center gap-2 text-sm font-medium text-gray-700">
          <FiEdit3 className="text-green-500" />
          <input
            type="range"
            min="1"
            max="12"
            value={width}
            onChange={(e) => setWidth(parseInt(e.target.value))}
            className="w-28 accent-blue-500"
          />
        </label>

        {/* Clear Button */}
        <button
          onClick={clearCanvas}
          className="flex items-center gap-2 bg-red-500 hover:bg-red-600 text-white px-3 py-2 rounded-md shadow font-medium"
        >
          <FiTrash2 />
          Clear
        </button>
      </div>
    </div>
  );
}
